   var sFileSt = fs.readFileSync('%saveFileName%');
   var currentSurveys = [];
   if (sFileSt.length === 0){
      currentSurveys = JSON.parse("[]");
   }else{
      currentSurveys = JSON.parse(sFileSt);
   } 

   currentSurveys.push(reqJSON);


    fs.writeFile('%saveFileName%', JSON.stringify(currentSurveys), function (err) {
    if (err) {
        console.log('There has been an error saving your json: ' +  "%saveFileName%" );
        console.log(err.message);									
        res.end(err);
        return;
     }else{
         res.end(JSON.stringify(reqJSON));
     }

   });
});

