function physics(){
    var movementDetect = false;
    elementArr.forEach(function(ele){

       if (isInViewport(ele)) {
            const eleOb = $("#"+ ele);
            var jEle = document.getElementById(ele);
            if ( jEle.hasAttribute( "dragging" )) {

            } else {
                var x = parseInt(eleOb[0].style.left, 10);
                var y = parseInt(eleOb[0].style.top, 10);

                 //gravty sucks X
                if(x > 1){
                    elementObjArr[ele].spdX -= 4
                }else if(x < -1){
                    elementObjArr[ele].spdX += 4
                }else if(Math.abs(elementObjArr[ele].spdX) < 12){
                    elementObjArr[ele].spdX = 0
                }

                //friction X
                elementObjArr[ele].spdX = elementObjArr[ele].spdX - elementObjArr[ele].spdX/10 

                //max speed X
                if(elementObjArr[ele].spdX > 100){ 
                    elementObjArr[ele].spdX = 100
                }else if(elementObjArr[ele].spdX < -100){
                    elementObjArr[ele].spdX = -100
                }


                //gravty sucks Y
                 if(y > 1){ 
                    elementObjArr[ele].spdY -= 4
                }else if(y < -1){
                    elementObjArr[ele].spdY += 4
                }else if(Math.abs(elementObjArr[ele].spdY) < 12){
                    elementObjArr[ele].spdY = 0
                }

                //friction Y
               elementObjArr[ele].spdY = elementObjArr[ele].spdY - elementObjArr[ele].spdY/10 

                //max speed Y
                if(elementObjArr[ele].spdY > 100){ 
                    elementObjArr[ele].spdY = 100
                }else if(elementObjArr[ele].spdY < -100){
                    elementObjArr[ele].spdY = -100
                }

                elementObjArr[ele].posX = x + elementObjArr[ele].spdX;
                elementObjArr[ele].posY = y + elementObjArr[ele].spdY;

                //console.log(JSON.stringify(elementObjArr["userIcon"]));

                if(Math.abs(elementObjArr[ele].spdX) > 0 || Math.abs(elementObjArr[ele].spdY) > 0 ){ 
                    movementDetect = true
                }

                if(!isNaN(x) && !isNaN(y)){
                 //   eleOb.css({'transition': 'top 0.1s linear, left 0.1s linear'});
                    eleOb.css({'top':elementObjArr[ele].posY + 'px'});
                    eleOb.css({'left':elementObjArr[ele].posX + 'px'});
                    // eleOb.css({'transform': 'translate('+elementObjArr[ele].posX+'px ,' +elementObjArr[ele].posY+ 'px)'});

                }else{
                    eleOb.css('top','0px');
                    eleOb.css('left','0px');
                }     
            }

       }

    })

    var nextTimeMs = 0;
    if(movementDetect){
        nextTimeMs = 50;
        afterPhysicsFunc();

    }else{
        nextTimeMs = 500;
        afterPhysicsFunc();
    }


    setTimeout(function(){ 
        physics();
    }, nextTimeMs);
}
