function %functionName%(){
    var mydata={'id':'#'};
    $.ajax({
        type: 'get',
        data: mydata,
        dataType : "text",
        headers: {
            "content-type": "application/json"
        },
        url: '%dataStoreGetService%',
        success : function(result) {

            var ResultsJson = JSON.parse(result);

            $("#%tableDivId%").html("") ;

            varRowHTML = '<div id="%rowClass%-mainCol" class="col">';
            varRowHTML += '</div>';
            $("#%tableDivId%").append(varRowHTML) ;

            ResultsJson.forEach(function(row){
                varRowHTML = '<div class="row %rowClass%" data-ID="'+row.id+'">';
                //if thumbnail exists and is not blank show thumbnail column and content col else only show content col
                if(row.hasOwnProperty("thumbnail")){
                    if(row.thumbnail !== ""){
                        varRowHTML += '<div class="col-6 %rowClass%-thumb-col"><div class="%rowClass%-thumb" data-ID="'+row.id+'" style="background-image:url('+row.%thumbData%+');" >';
                        // varRowHTML += '<img src="'+row.%thumbData%+'">'
                        varRowHTML += '</div></div>'
                        varRowHTML += '<div class="col-6 %rowClass%-contCol">';
                    }else{
                        varRowHTML += '<div class="col-12 %rowClass%-contCol">';
                    }   
                }else{
                    varRowHTML += '<div class="col-12 %rowClass%-contCol">';
                }


                    varRowHTML += '<div class="row %rowClass%-topRow">';
                        varRowHTML += '<div class="col %rowClass%-title" data-ID="'+row.id+'">';
                            varRowHTML += row.%titleData%;
                        varRowHTML += '</div>';
                    varRowHTML += '</div>';

                    varRowHTML += '<div class="row %rowClass%-authorRow">';
                        varRowHTML += '<div class="col-6 %rowClass%-author">';
                            varRowHTML += "By: " + row.%authorData%;
                        varRowHTML += '</div>';
                        varRowHTML += '<div class="col-6 %rowClass%-date">';
                            varRowHTML += "On: " + row.%dateData%;
                        varRowHTML += '</div>';
                    varRowHTML += '</div>';
                    varRowHTML += '<div class="row %rowClass%-contRow">';
                        varRowHTML += '<div data-ID="'+row.id+'" class="col %rowClass%-content">';

                        //update content block    
                        if(row.hasOwnProperty("%quillInput%")){
                            if(row.%quillInput%.hasOwnProperty("ops")){
                                varRowHTML += stripHtml(quillGetHTML(row.%quillInput%)) 
                            }
                        }   

                        varRowHTML += '</div>';
                    varRowHTML += '</div><p class="%rowClass%-dotdotdot">...</p>';
                varRowHTML += '</div>';

                $("#%rowClass%-mainCol").prepend(varRowHTML) ;
            })


            $('.%rowClass%').on('click', function(){
               var id = $(this).attr('data-ID');

               <!-- %rowClickJsInsert% -->
            })

        },
        error : function(error) {
            console.log(error)
        }
    })
}

function %getRowFunctionName%(rowId){
    $.get('%getRowServiceName%', { id: rowId }, function(data) {
       var rObj = data[0];    
	    $("#id").val(rObj.id);
       var fields='%fields%';
       var fieldsArr = fields.split(',');
       for(var x=0; x<fieldsArr.length;x++){
           if(rObj.hasOwnProperty(fieldsArr[x])){
               $("#"+fieldsArr[x]).text(rObj[fieldsArr[x]])
           }
       }
        //update quil    
        if(rObj.hasOwnProperty(rObj.%quillObj%)){
            if(rObj.%quillInput%.hasOwnProperty("ops")){
                %quillObj%.setContents(rObj.%quillInput%);
            }
        }   
        //update thumbnail
        // $("#%thumbDispId%").attr("src",rObj['%thumbInputId%']);
    })
}

