var spawn = require('child_process').spawn;

router.get("%prefix%%serviceRoute%",function(req,res){
    var tail = spawn('tail', ['-f', '-n%numOfLines%', '%fileName%']);
    res.setHeader('Connection', 'Transfer-Encoding');
    res.setHeader('Content-Type', 'text/event-stream; charset=utf-8');
    res.setHeader('Transfer-Encoding', 'chunked');

    //res.writeHead(200, {'Content-Type': 'text/plain' });
    req.on("close", function(){
        console.log("The /getlog client disconnected!");
        tail.kill();
        clearTimeout(to);
    });

    //send ping every 30 sec so browser doesn't give up on us and disconnect
    var to;
    function ping(res){
        console.log("The /getlog server is pinging!");
        res.write(JSON.stringify({ping:true})+"\n");
        to = setTimeout(ping,30000,res)
    }(res);
    ping(res)

    tail.stdout.on('data', function (data) {
      res.write(data);                
    });

});

