// Load the SDK for JavaScript
const AWS = require('aws-sdk');
const fs = require('fs');
const homedir = require('os').homedir();
const sec_group_ips = JSON.parse(fs.readFileSync("sec_group_ips.json"))
const thisIP = fs.readFileSync("thisIP.txt").toString().replace("\n","")

// Load credentials and set region from JSON file
AWS.config.loadFromPath(homedir + '/accessConfig.json');

// Set the region
AWS.config.update({region: '%region%'});

// Create EC2 service object
var ec2 = new AWS.EC2({apiVersion: '2018-03-20'});

// get default vpc id
var params = {
    AttributeNames: [
        "default-vpc"
    ]
};
ec2.describeAccountAttributes(params, function(err, data) {
    if (err) console.log(err, err.stack);
    else{
        const default_vpc = data.AccountAttributes[0].AttributeValues[0].AttributeValue;
        console.log("Default vpc for this account is: "+default_vpc);

        //Create sec group
        var rString = randomString(4);
        var params = {
            Description: 'Group to provide access to ezStack demo services.',
            GroupName: 'builder-demo-group ' + rString ,
            DryRun: false,
            VpcId: default_vpc
        };
        ec2.createSecurityGroup(params, function(err, data) {
            if (err){
                if(err.toString().indexOf("InvalidGroup.Duplicate") !== -1){
                    console.log("err: "+err);
                    var params = {
                        GroupNames: [
                            "builder-demo-group"
                        ]
                    };
                    ec2.describeSecurityGroups(params, function(err, data) {
                        console.log(data);
                        var sg_data = {GroupId:data.SecurityGroups[0].GroupId}
                        fs.writeFile('secGroupID.json', JSON.stringify(sg_data), function (err) {
                            if (err) {
                                console.log('There has been an error saving secGroupID json');
                                console.log(err.message);
                            }
                        });
                    });
                }else{
                    console.log("err: "+err);
                    console.log("stack: "+err.stack);
                }

            }else{
                //save in file
                fs.writeFile('secGroupID.json', JSON.stringify(data), function (err) {
                    if (err) {
                        console.log('There has been an error saving secGroupID json');
                        console.log(err.message);
                    }
                });

                const GroupId = data.GroupId;
                console.log('Added group: ' + 'builder-demo-group ' + rString );
                console.log('ID: ' + GroupId);
                var params = {
                    GroupId: GroupId,
                    IpPermissions: [{
                        IpProtocol: "tcp",
