//function to tag a given instance. Provide a tag name and a promise for a new instance
function createInstancesTags(instancePromise ){

    instancePromise.then(
        function(data) {
            data.Instances.forEach(function(instance){

                tagName = "ezStack Demo Instance";
                var instanceId = instance.InstanceId;
                console.log("Created instance", instanceId);

                // Add tags to the instance
                tagParams = {
                    Resources: [instanceId], Tags: [
                        {
                            Key: 'Name',
                            Value: tagName
                        }
                    ]
                };

                //create a tag promise
                var tagPromise = new AWS.EC2({apiVersion: '2016-11-15'}).createTags(tagParams).promise();

                //then describe instance, get inst id and save instList.json
                tagPromise.then(
                    function (data) {
                        console.log("Instance tagged: " + instanceId);

                        var DIparams = {
                            Filters: [
                                {
                                    Name: 'instance-id',
                                    Values: [instanceId]
                                }
                            ]
                        };

                        ec2.describeInstances(DIparams, function (err, data) {
                            if (err) return console.error(err.message);
                            instList.push({'inst': instanceId});

                            console.log({'inst': instanceId});
                            saveInstList();

                            function saveInstList() {
                                fs.writeFile('instList.json', JSON.stringify(instList), function (err) {
                                    if (err) {
                                        console.log('There has been an error saving Instance json');
                                        console.log(err.message);
                                    }
                                })
                            }
                        });

                    }).catch(
                    function (err) {
                        console.error(err, err.stack);
                    });


            })

        }).catch(
        function(err) {
            console.error(err, err.stack);
        });
}

// https://www.npmjs.com/package/acme-client

function log(m) {
    process.stdout.write(`${m}\n`);
}


/**
 * Function used to satisfy an ACME challenge
 *
 * @param {object} authz Authorization object
 * @param {object} challenge Selected challenge
 * @param {string} keyAuthorization Authorization key
 * @returns {Promise}
 */

async function challengeCreateFn(authz, challenge, keyAuthorization) {
    log('Triggered challengeCreateFn()');

    /* http-01 */
    if (challenge.type === 'http-01') {
        const filePath = '%staticPath%/.well-known/acme-challenge/' + challenge.token;
        const fileContents = keyAuthorization;

        log(`Creating challenge response for ${authz.identifier.value} at path: ${filePath}`);

        /* Replace this */
        log(`Would write "${fileContents}" to path "${filePath}"`);
        fs.writeFileSync(filePath, fileContents);
    }

    /* dns-01 */
    else if (challenge.type === 'dns-01') {
        const dnsRecord = `_acme-challenge.${authz.identifier.value}`;
        const recordValue = keyAuthorization;

        log(`Creating TXT record for ${authz.identifier.value}: ${dnsRecord}`);

        /* Replace this */
        log(`Would create TXT record "${dnsRecord}" with value "${recordValue}"`);
        // await dnsProvider.createRecord(dnsRecord, 'TXT', recordValue);
    }
}


/**
 * Function used to remove an ACME challenge response
 *
 * @param {object} authz Authorization object
 * @param {object} challenge Selected challenge
 * @param {string} keyAuthorization Authorization key
 * @returns {Promise}
 */

async function challengeRemoveFn(authz, challenge, keyAuthorization) {
    log('Triggered challengeRemoveFn()');

    /* http-01 */
    if (challenge.type === 'http-01') {
        const filePath = '%staticPath%/.well-known/acme-challenge/' + challenge.token;

        log(`Removing challenge response for ${authz.identifier.value} at path: ${filePath}`);

        /* Replace this */
        log(`Would remove file on path "${filePath}"`);
        fs.unlinkSync(filePath);
    }

    /* dns-01 */
    else if (challenge.type === 'dns-01') {
        const dnsRecord = `_acme-challenge.${authz.identifier.value}`;
        const recordValue = keyAuthorization;

        log(`Removing TXT record for ${authz.identifier.value}: ${dnsRecord}`);

        /* Replace this */
        log(`Would remove TXT record "${dnsRecord}" with value "${recordValue}"`);
        // await dnsProvider.removeRecord(dnsRecord, 'TXT');
    }
}


