f9b1ab6d-c47c-4290-beed-c389634c11a2.description:
provides runtime function:
function %functionName%(targetDiv, fields, linkField)
this calls rowClickFunctionName to set the onClick event code
seperate fields with ;

f9b1ab6d-c47c-4290-beed-c389634c11a2.template:
function %rowClickFunctionName%(){
    $('.%rowClass%').on('click', function(){
        var id = $(this).attr('data-ID');
        <!-- %rowClickJsInsert% -->
    })
}


function %functionName%(divHtmlToUpdate, fields, linkField){
    var mydata={'id':'#'};
    $.ajax({
        type: 'get',
        data: mydata,
        dataType : "text",
        headers: {
            "content-type": "application/json"
        },
        url: '/%dataStoreGetService%',
        success : function(result) {

            var ResultsJson = JSON.parse(result);

            var html = "<div class='varBody'><table style='width: 100%;' role='grid' class='table table-striped table-bordered table-hover dataTable no-footer dtr-inline' id='dataTables-example' width='100%'>";
            html += "<thead>";

            html += "<tr role='row'>"

            fields.split(';').forEach(function(fld){
                html += "<th style='width: 10%;' colspan='1' rowspan='1'>"+fld+"</th>"
            })
            html += "</tr>";
            html += "</thead>";
            html += "<tbody>";

            var x=1;

            ResultsJson.forEach(function(row){
                if(x%2 === 1){
                    html += "<tr role='row' class='gradeA odd' >";
                }else{
                    html += "<tr role='row' class='gradeA even' >";
                }
                x++;
                fields.split(';').forEach(function(fld){
                    if (typeof row[fld] !== 'undefined') {
                        if (fld === linkField) {
                            var txt=row[fld];
                            if(txt === ''){txt = '[none]'}
                            html += "<td  class='sorting_1' colspan=1  ><u class='%rowClass%' data-ID='" + row.id + "' data-fields='" + "%fields%" + "'>" + txt + '</u></td>'
                        } else {
                            html += "<td  class='sorting_1' colspan=1 width='75%' ><textarea readonly disabled rows='%textAreaRows%' class='form-control' style='width: 100%;'>" + row[fld] + '</textarea></td>'
                        }
                    }else{
                        if (fld === linkField) {
                            html += "<td  class='sorting_1' colspan=1  ><u class='%rowClass%' data-ID='" + row.id + "' data-fields='" + "%fields%" + "'>" + "[none]" + '</u></td>'
                        }
                    }

                })
                html += "</tr>"
            })

            html += "</tbody></table></div>";
            $("#"+divHtmlToUpdate).html(html) ;
            %rowClickFunctionName%();


        },
        error : function(error) {
            console.log(error)
        }
    })
}

