$(document).ready(function() {
    (function () {
        //define the animation refresh (frame rendering) with built-in browser timing
        window.requestAnimationFrame = function () {
            return window.requestAnimationFrame || window.webkitRequestAnimationFrame || window.mozRequestAnimationFrame || window.msRequestAnimationFrame || window.oRequestAnimationFrame || function (f) {
                    window.setTimeout(f, 1000 / 30)
                }
        }
    });

    var canvas = document.getElementById("myCanvas");

    var ctx = canvas.getContext('2d');

    var particles = [];
    var particle;

    wx = 750;
    wy = 630;
    var dimensions = {
        width: wx+5,
        height: wy
    };
    $('#myCanvas').css(dimensions).prop(dimensions);

    //var img = new Image();
    //img.src = 'images/3dCloudAWS2.png';

    var bcromb ='';
    var JsonArr = [
  {
	"id":"base system",
	sz:"20/20/1",
	"children":[
	  {
		"id":"project",
		sz:"20/20/1",
		"children":[
