8414bbb9-efb8-43e9-af18-fd1e1feafd61.template:
function %functionName%(fields){
        fields.split(';').forEach(function(field){
            var fieldObj = $("#" + field)[0];
            fieldObj.readOnly= true;
            fieldObj.disabled= true;
        })
}

