d2ecc2ef-11ad-48c6-8f56-ea192e16171b.template:
var mydata = {};

$('#%formId% input, #%formId% textarea').each(
    function(index){
        var input = $(this);
        mydata[input.attr('id')] = input.val();
    }
);

mydata.id = $("#id").val();

$.ajax({
    type: 'POST',
    data: JSON.stringify(mydata),
    dataType : "text",
    headers: {
        "content-type": "application/json",
    },
    url: '/%serviceName%',
    success : function(result) {
        //console.log('success');
<!-- %afterSaveInsert% -->
    },
    error : function(error) {
        console.log(error)
    }
});

