e6986a19-179c-4afb-bb78-8ec3d405b354.template:
#!/bin/sh
cd %projectPath%
pwd;
echo;
echo Stopping Server %serverName%..;
sudo forever stop %serverName%;
echo;
echo Starting Server %serverName%..;
sudo forever start -a -l forever.log -o out.log -e err.log %serverName%;
echo;
ps -ef | grep node;
echo;
forever list;
